import AuthTypes from "./auth.types"

const INITIAL_STATE = {
    isLoggedIn: false,
    userId: null,
    token: null
}

const authReducer = (state=INITIAL_STATE, action) => {
    switch(action.type){
        case AuthTypes.LOGIN:
            return {
                ...state,
                isLoggedIn: true,
                token: action.payload.token
            }

        case AuthTypes.SIGN_UP:
            return {
                ...state,
                isLoggedIn: true,
                
            }

        default:
            return state;
    }
}

export default authReducer;
