import React, { useRef, useState } from "react";
import { connect } from "react-redux";
import { useNavigate } from "react-router-dom";

import InputForm from "../components/input/Input.component";
import ButtonForm from "../components/button/Button.comonent";
import { useHttpCLient } from "../hooks/http-hook";

const SignUp = ({ token, onChange }) => {
  const { isLoading, error, sendRequest, clearError } = useHttpCLient();
  const navigate = useNavigate();
  const nameRef = useRef();
  const emailRef = useRef();
  const passwordRef = useRef();
  const imageRef = useRef();

  // const [nameValue, setNameValue] = useState("");
  // const [emailValue, setEmailValue] = useState("");
  // const [passwordValue, setPasswordValue] = useState("");
  // const [imagePath, setImagePath] = useState('');

  const signupHandler = async (event) => {
    event.preventDefault();
    try {
      const formData = new FormData();
      formData.append("name", nameRef.current.value);
      formData.append("email", emailRef.current.value);
      formData.append("password", nameRef.current.value);
      formData.append("image", nameRef.current.value);

      console.log(formData);

      await sendRequest("http://localhost:3001/api/signup", "POST", formData, {
        Authorization: "Bear" + token,
      });
      navigate("/");
    } catch (err) {}
  };

  return (
    <form className="sign-up" onSubmit={signupHandler}>
      <InputForm
        id="name"
        name="Name"
        type="text"
        ref={nameRef}
      />
      <InputForm
        id="email"
        name="Email"
        type="email"
        ref={emailRef}
      />
      <InputForm
        id="password"
        name="Password"
        type="password"
        ref={passwordRef}
      />

      <InputForm
        id="image"
        name="Avatar"
        type="file"
        ref={imageRef}
        onChange={(e) => onChange(e)}
      />

      <ButtonForm title="Sign Up" type="submit" />
    </form>
  );
};

const mapStateToProps = (state) => ({
  token: state.auth.token,
});

export default connect(mapStateToProps)(SignUp);
